import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ProfileList from './components/ProfileList';
import ProfileDetail from './components/ProfileDetail';
import ProfileForm from './components/ProfileForm';
import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';

const App = () => {
  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/">
            <h1>Welcome to Tech Pioneers</h1>
            <RegisterForm />
          </Route>
          <Route exact path="/profiles">
            <ProfileList />
          </Route>
          <Route exact path="/profiles/:id">
            {({ match }) => <ProfileDetail match={match} token={localStorage.getItem('token')} />}
          </Route>
          <Route exact path="/edit-profile/:id">
            {({ match }) => <ProfileForm profileId={match.params.id} token={localStorage.getItem('token')} />}
          </Route>
          <Route exact path="/login">
            {({ history }) => <LoginForm onLogin={(token) => {
              localStorage.setItem('token', token);
              history.push('/profiles');
            }} />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
